SELECT SUM("catalog_sales1374"."cs_ext_discount_amt") AS "excess discount amount" FROM "catalog_sales" AS "catalog_sales1374" INNER JOIN (SELECT * FROM "item" WHERE "i_manufact_id" = 722) AS "t41547" ON "catalog_sales1374"."cs_item_sk" = "t41547"."i_item_sk" INNER JOIN (SELECT * FROM "date_dim" WHERE "d_date" >= DATE '2001-03-09' AND "d_date" <= (DATE '2001-03-09' + INTERVAL '90' DAY)) AS "t41548" ON "catalog_sales1374"."cs_sold_date_sk" = "t41548"."d_date_sk" INNER JOIN (SELECT "catalog_sales1375"."cs_item_sk", AVG("catalog_sales1375"."cs_ext_discount_amt") AS "f1" FROM "catalog_sales" AS "catalog_sales1375" INNER JOIN (SELECT * FROM "date_dim" WHERE "d_date" >= DATE '2001-03-09' AND "d_date" <= (DATE '2001-03-09' + INTERVAL '90' DAY)) AS "t41549" ON "catalog_sales1375"."cs_sold_date_sk" = "t41549"."d_date_sk" GROUP BY "catalog_sales1375"."cs_item_sk") AS "t41550" ON "t41547"."i_item_sk" = "t41550"."cs_item_sk" AND "catalog_sales1374"."cs_ext_discount_amt" > 1.3 * "t41550"."f1" FETCH NEXT 100 ROWS ONLY