SELECT 1998 AS "d_year", "t55284"."i_category_id", "t55284"."i_category", SUM("t55282"."EXPR3" * "t55284"."f3") FROM (SELECT "t55280"."ss_item_sk", SUM("t55279"."f1" * "t55280"."EXPR3") AS "EXPR3" FROM (SELECT "d_date_sk", COUNT(*) AS "f1" FROM "date_dim" WHERE "d_moy" = 11 AND "d_year" = 1998 GROUP BY "d_date_sk") AS "t55279" INNER JOIN (SELECT "ss_sold_date_sk", "ss_item_sk", SUM("ss_ext_sales_price") AS "EXPR3" FROM "store_sales" GROUP BY "ss_sold_date_sk", "ss_item_sk") AS "t55280" ON "t55279"."d_date_sk" = "t55280"."ss_sold_date_sk" GROUP BY "t55280"."ss_item_sk") AS "t55282" INNER JOIN (SELECT "i_item_sk", "i_category_id", "i_category", COUNT(*) AS "f3" FROM "item" WHERE "i_manager_id" = 1 GROUP BY "i_item_sk", "i_category_id", "i_category") AS "t55284" ON "t55282"."ss_item_sk" = "t55284"."i_item_sk" GROUP BY "t55284"."i_category_id", "t55284"."i_category" ORDER BY SUM("t55282"."EXPR3" * "t55284"."f3") DESC, "t55284"."i_category_id", "t55284"."i_category" FETCH NEXT 100 ROWS ONLY