SELECT 2000 AS "d_year", "t69701"."i_brand_id" AS "brand_id", "t69701"."i_brand" AS "brand", SUM("t69699"."ext_price" * "t69701"."f3") AS "ext_price" FROM (SELECT "t69697"."ss_item_sk", SUM("t69696"."f1" * "t69697"."ext_price") AS "ext_price" FROM (SELECT "d_date_sk", COUNT(*) AS "f1" FROM "date_dim" WHERE "d_moy" = 12 AND "d_year" = 2000 GROUP BY "d_date_sk") AS "t69696" INNER JOIN (SELECT "ss_sold_date_sk", "ss_item_sk", SUM("ss_ext_sales_price") AS "ext_price" FROM "store_sales" GROUP BY "ss_sold_date_sk", "ss_item_sk") AS "t69697" ON "t69696"."d_date_sk" = "t69697"."ss_sold_date_sk" GROUP BY "t69697"."ss_item_sk") AS "t69699" INNER JOIN (SELECT "i_item_sk", "i_brand_id", "i_brand", COUNT(*) AS "f3" FROM "item" WHERE "i_manager_id" = 1 GROUP BY "i_item_sk", "i_brand_id", "i_brand") AS "t69701" ON "t69699"."ss_item_sk" = "t69701"."i_item_sk" GROUP BY "t69701"."i_brand_id", "t69701"."i_brand" ORDER BY SUM("t69699"."ext_price" * "t69701"."f3") DESC, "t69701"."i_brand_id" FETCH NEXT 100 ROWS ONLY