SELECT "t72185"."segment", COUNT(*) AS "num_customers", "t72185"."segment" * 50 AS "segment_base" FROM (SELECT CAST(SUM("store_sales6393"."ss_ext_sales_price") / 50 AS INTEGER) AS "segment" FROM (SELECT "t72174"."c_customer_sk", "t72174"."c_current_addr_sk" FROM (SELECT "t72170"."cs_bill_customer_sk" FROM (SELECT "t72167"."cs_sold_date_sk", "t72167"."cs_bill_customer_sk" FROM (SELECT "cs_sold_date_sk", "cs_bill_customer_sk", "cs_item_sk" FROM (SELECT "cs_sold_date_sk", "cs_bill_customer_sk", "cs_item_sk" FROM "catalog_sales" GROUP BY "cs_sold_date_sk", "cs_bill_customer_sk", "cs_item_sk" UNION ALL SELECT "ws_sold_date_sk", "ws_bill_customer_sk", "ws_item_sk" FROM "web_sales" GROUP BY "ws_sold_date_sk", "ws_item_sk", "ws_bill_customer_sk") AS "t72166" GROUP BY "cs_sold_date_sk", "cs_bill_customer_sk", "cs_item_sk") AS "t72167" INNER JOIN (SELECT "i_item_sk" FROM "item" WHERE "i_category" = 'Music                                             ' AND "i_class" = 'country                                           ' GROUP BY "i_item_sk") AS "t72169" ON "t72167"."cs_item_sk" = "t72169"."i_item_sk" GROUP BY "t72167"."cs_sold_date_sk", "t72167"."cs_bill_customer_sk") AS "t72170" INNER JOIN (SELECT "d_date_sk" FROM "date_dim" WHERE "d_year" = 1999 AND "d_moy" = 1 GROUP BY "d_date_sk") AS "t72172" ON "t72170"."cs_sold_date_sk" = "t72172"."d_date_sk" GROUP BY "t72170"."cs_bill_customer_sk") AS "t72173" INNER JOIN (SELECT "c_customer_sk", "c_current_addr_sk" FROM "customer" GROUP BY "c_customer_sk", "c_current_addr_sk") AS "t72174" ON "t72173"."cs_bill_customer_sk" = "t72174"."c_customer_sk") AS "t72175" INNER JOIN "store_sales" AS "store_sales6393" ON "t72175"."c_customer_sk" = "store_sales6393"."ss_customer_sk" INNER JOIN "customer_address" AS "customer_address2348" ON "t72175"."c_current_addr_sk" = "customer_address2348"."ca_address_sk" INNER JOIN "store" AS "store1655" ON "customer_address2348"."ca_county" = "store1655"."s_county" AND "customer_address2348"."ca_state" = "store1655"."s_state" INNER JOIN "date_dim" AS "date_dim10007" ON "store_sales6393"."ss_sold_date_sk" = "date_dim10007"."d_date_sk" INNER JOIN (SELECT SINGLE_VALUE("EXPR0") AS "f0" FROM (SELECT "d_month_seq" + 1 AS "EXPR0" FROM "date_dim" WHERE "d_year" = 1999 AND "d_moy" = 1 GROUP BY "d_month_seq" + 1) AS "t72178") AS "t72179" ON "date_dim10007"."d_month_seq" >= "t72179"."f0" INNER JOIN (SELECT SINGLE_VALUE("EXPR0") AS "f0" FROM (SELECT "d_month_seq" + 3 AS "EXPR0" FROM "date_dim" WHERE "d_year" = 1999 AND "d_moy" = 1 GROUP BY "d_month_seq" + 3) AS "t72182") AS "t72183" ON "date_dim10007"."d_month_seq" <= "t72183"."f0" GROUP BY "t72175"."c_customer_sk") AS "t72185" GROUP BY "t72185"."segment" ORDER BY "t72185"."segment", COUNT(*) FETCH NEXT 100 ROWS ONLY