SELECT "t74913"."i_brand_id" AS "brand_id", "t74913"."i_brand" AS "brand", SUM("t74911"."ext_price" * "t74913"."f3") AS "ext_price" FROM (SELECT "t74909"."ss_item_sk", SUM("t74908"."f1" * "t74909"."ext_price") AS "ext_price" FROM (SELECT "d_date_sk", COUNT(*) AS "f1" FROM "date_dim" WHERE "d_moy" = 11 AND "d_year" = 2000 GROUP BY "d_date_sk") AS "t74908" INNER JOIN (SELECT "ss_sold_date_sk", "ss_item_sk", SUM("ss_ext_sales_price") AS "ext_price" FROM "store_sales" GROUP BY "ss_sold_date_sk", "ss_item_sk") AS "t74909" ON "t74908"."d_date_sk" = "t74909"."ss_sold_date_sk" GROUP BY "t74909"."ss_item_sk") AS "t74911" INNER JOIN (SELECT "i_item_sk", "i_brand_id", "i_brand", COUNT(*) AS "f3" FROM "item" WHERE "i_manager_id" = 52 GROUP BY "i_item_sk", "i_brand_id", "i_brand") AS "t74913" ON "t74911"."ss_item_sk" = "t74913"."i_item_sk" GROUP BY "t74913"."i_brand_id", "t74913"."i_brand" ORDER BY SUM("t74911"."ext_price" * "t74913"."f3") DESC, "t74913"."i_brand_id" FETCH NEXT 100 ROWS ONLY