OR ("d_month_seq" = 1222 + 9 OR ("d_month_seq" = 1222 + 10 OR "d_month_seq" = 1222 + 11))) GROUP BY "d_date_sk", "d_moy") AS "t94993" ON "t94991"."ss_sold_date_sk" = "t94993"."d_date_sk" GROUP BY "t94991"."i_manager_id", "t94991"."ss_store_sk", "t94993"."d_moy") AS "t94995" INNER JOIN (SELECT "s_store_sk", COUNT(*) AS "f1" FROM "store" GROUP BY "s_store_sk") AS "t94996" ON "t94995"."ss_store_sk" = "t94996"."s_store_sk" GROUP BY "t94995"."i_manager_id", "t94995"."d_moy") AS "t94998") AS "t94999" WHERE CASE WHEN "avg_monthly_sales" > 0 THEN ABS("sum_sales" - "avg_monthly_sales") / "avg_monthly_sales" > 0.1 ELSE FALSE END ORDER BY "i_manager_id", "avg_monthly_sales", "sum_sales" FETCH NEXT 100 ROWS ONLY