SELECT SUM("web_sales3582"."ws_ext_discount_amt") AS "Excess Discount Amount" FROM "web_sales" AS "web_sales3582" INNER JOIN (SELECT * FROM "item" WHERE "i_manufact_id" = 714) AS "t131330" ON "web_sales3582"."ws_item_sk" = "t131330"."i_item_sk" INNER JOIN (SELECT * FROM "date_dim" WHERE "d_date" >= DATE '2000-02-01' AND "d_date" <= (DATE '2000-02-01' + INTERVAL '90' DAY)) AS "t131331" ON "web_sales3582"."ws_sold_date_sk" = "t131331"."d_date_sk" INNER JOIN (SELECT "web_sales3583"."ws_item_sk", AVG("web_sales3583"."ws_ext_discount_amt") AS "f1" FROM "web_sales" AS "web_sales3583" INNER JOIN (SELECT * FROM "date_dim" WHERE "d_date" >= DATE '2000-02-01' AND "d_date" <= (DATE '2000-02-01' + INTERVAL '90' DAY)) AS "t131332" ON "web_sales3583"."ws_sold_date_sk" = "t131332"."d_date_sk" GROUP BY "web_sales3583"."ws_item_sk") AS "t131333" ON "t131330"."i_item_sk" = "t131333"."ws_item_sk" AND "web_sales3582"."ws_ext_discount_amt" > 1.3 * "t131333"."f1" ORDER BY SUM("web_sales3582"."ws_ext_discount_amt") FETCH NEXT 100 ROWS ONLY