SELECT "store_sales9696"."ss_customer_sk", SUM(CASE WHEN "store_returns1289"."sr_return_quantity" IS NOT NULL THEN ("store_sales9696"."ss_quantity" - "store_returns1289"."sr_return_quantity") * "store_sales9696"."ss_sales_price" ELSE "store_sales9696"."ss_quantity" * "store_sales9696"."ss_sales_price" END) AS "sumsales" FROM "store_sales" AS "store_sales9696" LEFT JOIN "store_returns" AS "store_returns1289" ON "store_sales9696"."ss_item_sk" = "store_returns1289"."sr_item_sk" AND "store_sales9696"."ss_ticket_number" = "store_returns1289"."sr_ticket_number" INNER JOIN (SELECT * FROM "reason" WHERE "r_reason_desc" = 'reason 58                                                                                           ') AS "t131539" ON "store_returns1289"."sr_reason_sk" = "t131539"."r_reason_sk" GROUP BY "store_sales9696"."ss_customer_sk" ORDER BY SUM(CASE WHEN "store_returns1289"."sr_return_quantity" IS NOT NULL THEN ("store_sales9696"."ss_quantity" - "store_returns1289"."sr_return_quantity") * "store_sales9696"."ss_sales_price" ELSE "store_sales9696"."ss_quantity" * "store_sales9696"."ss_sales_price" END), "store_sales9696"."ss_customer_sk" FETCH NEXT 100 ROWS ONLY