SELECT COALESCE(SUM("t135329"."EXPR0" * "t135331"."EXPR0"), 0) FROM (SELECT "t135325"."ss_store_sk", COALESCE(SUM("t135325"."EXPR0" * "t135327"."EXPR0"), 0) AS "EXPR0" FROM (SELECT "t135321"."ss_sold_time_sk", "t135321"."ss_store_sk", COALESCE(SUM("t135321"."EXPR0" * "t135323"."EXPR0"), 0) AS "EXPR0" FROM (SELECT "ss_sold_time_sk", "ss_hdemo_sk", "ss_store_sk", COUNT(*) AS "EXPR0" FROM "store_sales" GROUP BY "ss_sold_time_sk", "ss_hdemo_sk", "ss_store_sk") AS "t135321" INNER JOIN (SELECT "hd_demo_sk", COUNT(*) AS "EXPR0" FROM "household_demographics" WHERE "hd_dep_count" = 0 GROUP BY "hd_demo_sk") AS "t135323" ON "t135321"."ss_hdemo_sk" = "t135323"."hd_demo_sk" GROUP BY "t135321"."ss_sold_time_sk", "t135321"."ss_store_sk") AS "t135325" INNER JOIN (SELECT "t_time_sk", COUNT(*) AS "EXPR0" FROM "time_dim" WHERE "t_hour" = 8 AND "t_minute" >= 30 GROUP BY "t_time_sk") AS "t135327" ON "t135325"."ss_sold_time_sk" = "t135327"."t_time_sk" GROUP BY "t135325"."ss_store_sk") AS "t135329" INNER JOIN (SELECT "s_store_sk", COUNT(*) AS "EXPR0" FROM "store" WHERE "s_store_name" = 'ese' GROUP BY "s_store_sk") AS "t135331" ON "t135329"."ss_store_sk" = "t135331"."s_store_sk" ORDER BY COALESCE(SUM("t135329"."EXPR0" * "t135331"."EXPR0"), 0) FETCH NEXT 100 ROWS ONLY