select  b1_lp,
        b1_cnt,
        b1_cntd,
        b2_lp,
        b2_cnt,
        b2_cntd,
        b3_lp,
        b3_cnt,
        b3_cntd,
        b4_lp,
        b4_cnt,
        b4_cntd,
        b5_lp,
        b5_cnt,
        b5_cntd,
        b6_lp,
        b6_cnt,
        b6_cntd
from
(
	select  avg(case when ss_quantity between 0 and 5 and ( ss_list_price between 107 and 117 or ss_coupon_amt between 1319 and 2319 or ss_wholesale_cost between 60 and 80 ) then ss_list_price end) as b1_lp,
	        count(case when ss_quantity between 0 and 5 and ( ss_list_price between 107 and 117 or ss_coupon_amt between 1319 and 2319 or ss_wholesale_cost between 60 and 80 ) then ss_list_price end) as b1_cnt,
	        count(distinct case when ss_quantity between 0 and 5 and ( ss_list_price between 107 and 117 or ss_coupon_amt between 1319 and 2319 or ss_wholesale_cost between 60 and 80 ) then ss_list_price end) as b1_cntd,
	        avg(case when ss_quantity between 6 and 10 and ( ss_list_price between 23 and 33 or ss_coupon_amt between 825 and 1825 or ss_wholesale_cost between 43 and 63 ) then ss_list_price end) as b2_lp,
	        count(case when ss_quantity between 6 and 10 and ( ss_list_price between 23 and 33 or ss_coupon_amt between 825 and 1825 or ss_wholesale_cost between 43 and 63 ) then ss_list_price end) as b2_cnt,
	        count(distinct case when ss_quantity between 6 and 10 and ( ss_list_price between 23 and 33 or ss_coupon_amt between 825 and 1825 or ss_wholesale_cost between 43 and 63 ) then ss_list_price end) as b2_cntd,
	        avg(case when ss_quantity between 11 and 15 and ( ss_list_price between 74 and 84 or ss_coupon_amt between 4381 and 5381 or ss_wholesale_cost between 57 and 77 ) then ss_list_price end) as b3_lp,
	        count(case when ss_quantity between 11 and 15 and ( ss_list_price between 74 and 84 or ss_coupon_amt between 4381 and 5381 or ss_wholesale_cost between 57 and 77 ) then ss_list_price end) as b3_cnt,
	        count(distinct case when ss_quantity between 11 and 15 and ( ss_list_price between 74 and 84 or ss_coupon_amt between 4381 and 5381 or ss_wholesale_cost between 57 and 77 ) then ss_list_price end) as b3_cntd,
	        avg(case when ss_quantity between 16 and 20 and ( ss_list_price between 89 and 99 or ss_coupon_amt between 3117 and 4117 or ss_wholesale_cost between 68 and 88 ) then ss_list_price end) as b4_lp,
	        count(case when ss_quantity between 16 and 20 and ( ss_list_price between 89 and 99 or ss_coupon_amt between 3117 and 4117 or ss_wholesale_cost between 68 and 88 ) then ss_list_price end) as b4_cnt,
	        count(distinct case when ss_quantity between 16 and 20 and ( ss_list_price between 89 and 99 or ss_coupon_amt between 3117 and 4117 or ss_wholesale_cost between 68 and 88 ) then ss_list_price end) as b4_cntd,
	        avg(case when ss_quantity between 21 and 25 and ( ss_list_price between 58 and 68 or ss_coupon_amt between 9402 and 10402 or ss_wholesale_cost between 38 and 58 ) then ss_list_price end) as b5_lp,
	        count(case when ss_quantity between 21 and 25 and ( ss_list_price between 58 and 68 or ss_coupon_amt between 9402 and 10402 or ss_wholesale_cost between 38 and 58 ) then ss_list_price end) as b5_cnt,
	        count(distinct case when ss_quantity between 21 and 25 and ( ss_list_price between 58 and 68 or ss_coupon_amt between 9402 and 10402 or ss_wholesale_cost between 38 and 58 ) then ss_list_price end) as b5_cntd,
	        avg(case when ss_quantity between 26 and 30 and ( ss_list_price between 64 and 74 or ss_coupon_amt between 5792 and 6792 or ss_wholesale_cost between 73 and 93 ) then ss_list_price end) as b6_lp,
	        count(case when ss_quantity between 26 and 30 and ( ss_list_price between 64 and 74 or ss_coupon_amt between 5792 and 6792 or ss_wholesale_cost between 73 and 93 ) then ss_list_price end) as b6_cnt,
	        count(distinct case when ss_quantity between 26 and 30 and ( ss_list_price between 64 and 74 or ss_coupon_amt between 5792 and 6792 or ss_wholesale_cost between 73 and 93 ) then ss_list_price end) as b6_cntd
	from store_sales
) as subquery
limit 100;